VALUES	('de_DE',	'LOC_PROJECT_JNR_GOV_CAPITAL_MOVE_NAME',				'Hauptstadt zur Regierung verlegen'),
		('de_DE',	'LOC_PROJECT_JNR_GOV_CAPITAL_MOVE_SHORT_NAME',			'Hauptstadt zur Regierung verlegen'),
		('de_DE',	'LOC_PROJECT_JNR_GOV_CAPITAL_MOVE_DESCRIPTION',			'Nach Fertigstellung wird die [ICON_Capital] Hauptstadt in diese Stadt verlegt. Kann nur einmal abgeschlossen werden.'),
		('de_DE',	'LOC_BUILDING_GOV_JNR_MOCC_NAME',						'Gemeinsames Einsatzkommando'),
		('de_DE',	'LOC_BUILDING_GOV_JNR_MOCC_DESCRIPTION',				'Gewährt +1 [Icon_Governor] Gouverneurstitel.[NEWLINE]+1 Militär-Politikplatz.[NEWLINE]+3 [ICON_Movement] Bewegung und +2 [ICON_Range] Reichweite für Lufteinheiten.[NEWLINE]+15 [ICON_Strength] Kampfstärke für Spez-Ops bei Verteidigung, wenn angrenzend an eine befreundete Mechanisierte Infanterie. +150% Flankierungsbonus für Spez-Ops beim Angriff, angrenzend an einen befreundeten Helikopter.[NEWLINE]+5 LP Heilung für Landeinheiten.[NEWLINE]Gewährt +3 Loyalität pro Runde für alle Städte mit stationerter Drohne in oder um das Stadtzentrum.'),
		('de_DE',	'LOC_BUILDING_GOV_JNR_PROPAGANDA_NAME',					'Kultureller Missionsverein'),
		('de_DE',	'LOC_BUILDING_GOV_JNR_PROPAGANDA_DESCRIPTION',			'Gewährt +1 [Icon_Governor] Gouverneurstitel.[NEWLINE]+1 Wirtschafts-Politikplatz.[NEWLINE]Alle  erstellten Rockbands erhalten die Religiöser Rock Beförderung zusätzlich zu einer eigens gewählten zweiten Beförderung.[NEWLINE]Spione erledigen Missionen in fremden Städten, die der eigenen Religion folgen, wie mit 2 Stufen mehr Erfahrung.[NEWLINE]Religiöse Einheiten sind getarnt und ignorieren die Kontrollzone.[NEWLINE]Gewährt +2 Loyalität pro Runde für alle eigenen Städte.[NEWLINE]'),
		('de_DE',	'LOC_BUILDING_GOV_JNR_DIPLOMACY_NAME',					'Weltkongressagentur'),
		('de_DE',	'LOC_BUILDING_GOV_JNR_DIPLOMACY_DESCRIPTION',			'Gewährt +1 [Icon_Governor] Gouverneurstitel.[NEWLINE]+1 Diplomatie-Politikplatz.[NEWLINE][NEWLINE]Etablierte [ICON_GOVERNOR] Gouverneure mit mindesten 4 Beförderungen gewähren +4 [ICON_Favor] Diplomatische Gunst.[NEWLINE]Wenn eine Resolution gewonnen und Diplomatische Siegpunkte im Weltkongress erhalten wurde, wird 25% der eingesetzten [ICON_Favor] Diplomatischen Gunst erstattet.[NEWLINE]Gewährt 1 Diplomatischen Siegpunkt.'),
		('de_DE',	'LOC_POLICY_JNR_CORPORATE_LIBERTARIANISM_NAME',			'Unternehmensliberales Vermächtnis'),
		('de_DE',	'LOC_POLICY_JNR_DIGITAL_DEMOCRACY_NAME',				'Digital-Demokratisches Vermächtnis'),
		('de_DE',	'LOC_POLICY_JNR_SYNTHETIC_TECHNOCRACY_NAME',			'Synthetisch-Technokratisches Vermächtnis'),
