		('de_DE',	'LOC_POLICY_JNR_DIGITAL_DEMOCRACY_NAME_RWF',			'Cyberdemokratisches Vermächtnis'),
		('de_DE',	'LOC_POLICY_JNR_SYNTHETIC_TECHNOCRACY_NAME_RWF',		'Technokratisches Vermächtnis'),
