		('de_DE',	'LOC_ABILITY_JNR_GOV_MOCC_SPEC_OPS_NAME',				'Erwarte die Kavallerie'),
		('de_DE',	'LOC_ABILITY_JNR_GOV_MOCC_SPEC_OPS_DESCRIPTION',		'+15 [ICON_Strength] Kampfstärke bei Verteidigung neben befreundeter Mechanisierter Infanterie. +150% Flankierungsbonus beim Angriff neben einem befreundeten Helikopter.'),
		('de_DE',	'LOC_ABILITY_JNR_GOV_MOCC_AIRCRAFT_NAME',				'Luftbetankung'),
