INSERT OR REPLACE INTO LocalizedText 	(Language,	Tag, 	Text) VALUES			
('de_DE', 	'LOC_IMPROVEMENT_FORT_DESCRIPTION_JNR_MC',					'Schaltet die Fähigkeit des Militäringenieurs frei, eine Festung zu bauen.[NEWLINE][NEWLINE]+1 [ICON_Production] Produktion. [NEWLINE]+1 [ICON_Culture] Kultur, wenn eine militärische Landeinheit auf diesem Feld stationiert ist.[NEWLINE]+1 [ICON_Production] Produktion, wenn eine Unterstützungseinheit auf dem Feld stationiert ist.[NEWLINE]+0.5 [ICON_Housing] Wohnraum.[NEWLINE][NEWLINE]{LOC_IMPROVEMENT_FORT_DESCRIPTION}'),
('de_DE', 	'LOC_IMPROVEMENT_ROMAN_FORT_DESCRIPTION_JNR_MC',			'Schaltet die Fähigkeit der Römischen Legion frei, ein römisches Fort zu bauen, das einzigartig für Rom ist.[NEWLINE][NEWLINE]+1 [ICON_Production] Produktion. [NEWLINE]+1 [ICON_Culture] Kultur, wenn eine militärische Landeinheit auf diesem Feld stationiert ist.[NEWLINE]+1 [ICON_Production] Produktion, wenn eine Unterstützungseinheit auf dem Feld stationiert ist.[NEWLINE]+1 [ICON_Housing] Wohnraum.[NEWLINE][NEWLINE]{LOC_IMPROVEMENT_FORT_DESCRIPTION}'),
('de_DE', 	'LOC_IMPROVEMENT_MAORI_PA_DESCRIPTION_JNR_MC',				'Schaltet die Fähigkeit der Toa frei, einen Pā zu bauen, die nur die Maori besitzen.[NEWLINE][NEWLINE]+1 [ICON_FOOD] Nahrung.[NEWLINE]+1 [ICON_Culture] Kultur, wenn eine militärische Landeinheit auf diesem Feld stationiert ist.[NEWLINE]+1 [ICON_Culture] Kultur, wenn eine Unterstützungseinheit auf diesem Feld stationiert ist.[NEWLINE]+1 [ICON_Housing] Wohnraum.[NEWLINE][NEWLINE]{LOC_IMPROVEMENT_FORT_DESCRIPTION} Eine Maori-Einheit, die einen Pā besetzt, wird geheilt, selbst wenn sie sich bewegt oder angegriffen hat. Muss auf einem Hügel errichtet werden.'),
('de_DE', 	'LOC_IMPROVEMENT_CHATEAU_DESCRIPTION_JNR_MC',				'{LOC_IMPROVEMENT_CHATEAU_DESCRIPTION}[NEWLINE]+1 [ICON_Housing] Wohnraum.[NEWLINE][NEWLINE]Die besetzende Einheit erhält +2 [ICON_Strength] Verteidigungsstärke und automatisch 1 Runde Verschanzung.'),
('de_DE', 	'LOC_IMPROVEMENT_CHATEAU_EXPANSION2_DESCRIPTION_JNR_MC',	'{LOC_IMPROVEMENT_CHATEAU_EXPANSION2_DESCRIPTION}[NEWLINE]+1 [ICON_Housing] Wohnraum.[NEWLINE][NEWLINE]Die besetzende Einheit erhält +2 [ICON_Strength] Verteidigungsstärke und automatisch 1 Runde Verschanzung.'),
('de_DE', 	'LOC_IMPROVEMENT_ALCAZAR_DESCRIPTION_JNR_MC',				'Schaltet die Fähigkeit des Handwerkers frei, einen Alcazar zu bauen.[NEWLINE][NEWLINE]+1 [ICON_Culture] Kultur, +1 [ICON_Science] Wissenschaft und +1 [ICON_Food] Nahrung.[NEWLINE]Bietet [ICON_Culture] Kultur in Höhe von 25% der Anziehungskraft des Feldes.[NEWLINE]+1 [ICON_Culture] Kultur, wenn eine militärische Landeinheit auf diesem Feld stationiert ist.[NEWLINE]+1 [ICON_Production] Produktion, wenn eine Unterstützungseinheit auf diesem Feld stationiert ist.[NEWLINE]Bietet [ICON_TOURISM] Tourismus nach Erforschung des Fluges.[NEWLINE]+1 [ICON_Amenities] Annehmlichkeiten, wenn ein Gouverneur in der Stadt ist. +1 [ICON_Housing] Wohnraum. +1 Anziehungskraft.[NEWLINE][NEWLINE]Die besetzende Einheit erhält +4 [ICON_Strength] Verteidigungsstärke und erhält automatisch 2 Runden Verschanzung.[NEWLINE][NEWLINE]Kann nicht neben einem anderen Alcazar gebaut werden.[NEWLINE]Nur ein Alcazar pro Stadt erlaubt.'),
('de_DE', 	'LOC_ABILITY_JNR_FORT_GARRISON_CULTURE_NAME',				'Festungsgarnison Kulturbonus'),
('de_DE', 	'LOC_ABILITY_JNR_FORT_GARRISON_CULTURE_NAME',				'Festungsgarnison Produktionsbonus');