-- Policies and Governments
UPDATE LocalizedText SET 'Text'='Eure [ICON_TradeRoute] Handelswege zu verbündeten Städten bieten +2 [ICON_Food] und +2 [ICON_Production] für beide Städte. Allianzpunkte durch jeden Verbündeten steigen um zusätzlich 1 Punkt pro Runde.'
WHERE Language='de_DE' AND Tag='LOC_POLICY_WISSELBANKEN_DESCRIPTION';

UPDATE LocalizedText SET 'Text'='Eure [ICON_TradeRoute] Handelswege zur Stadt eines Verbündeten oder Suzeräns bieten +4 [ICON_Food] Nahrung und +4 [ICON_Production] Produktion für beide Städte. Allianzpunkte durch jeden Verbündeten steigen um zusätzlich 1 Punkt pro Runde.'
WHERE Language='de_DE' AND Tag='LOC_GOVT_INHERENT_BONUS_DEMOCRACY_XP2';
