INSERT OR REPLACE INTO LocalizedText
		(Tag,															Language,	Text)
VALUES	('LOC_BUILDING_ARENA_DESCRIPTION_UC_JNR',						'de_DE',	'+1 [ICON_Amenities] Annehmlichkeiten, wenn diese Stadt einen etablierten Gouverneur hat. Turnierplätze innerhalb von 6 Feldern um das Stadtzentrum geben keinen weiteren Bonus.'),
		('LOC_BUILDING_JNR_TOURNEY_DESCRIPTION',						'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Feldern ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Turnierplätze oder Arenen innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_Tourism] Tourismus für jeden Verbündeten, der eine Handelsroute zu dieser Stadt hat.'),
		('LOC_BUILDING_TLACHTLI_DESCRIPTION_UC_JNR',					'de_DE',	'Ein einzigartiges Gebäude der Azteken, welches die Arena sowie den Turnierplatz ersetzt.[NEWLINE]Liefert +1 [ICON_Amenities] Annehmlichkeit und zusätzlich +1 [ICON_Amenities] Annehmlichkeit wenn im Krieg. +1 [ICON_Culture] Kultur und +1 [ICON_GreatGeneral] Großer-General-Punkt. +1 [ICON_FAITH] Glauben für jede ursprüngliche Hauptstadt einer Zivilisation im Besitz.'),
		('LOC_BUILDING_ZOO_DESCRIPTION_UC_JNR',							'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Feldern ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Botanische Gärten innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_Science] Wissenschaft für jedes Ressourcenfeld mit Weide oder Lager innerhalb dieser Stadt.'),
		('LOC_BUILDING_JNR_BOTANICAL_GARDEN_DESCRIPTION',				'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Feldern ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Zoos innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_Science] Wissenschaft für jedes Ressourcenfeld mit Bauernhof, Plantage oder Sägewerk innerhalb dieser Stadt.[NEWLINE]+2 [ICON_Tourism] Tourismus.'),
		('LOC_BUILDING_THERMAL_BATH_DESCRIPTION_UC_JNR',				'de_DE',	'Ein einzigartiges Gebäude Ungarns, welches den Zoo und den Botanischen Garten ersetzt.[NEWLINE]+1 [ICON_AMENITIES] Annehmlichkeit und +2 [ICON_PRODUCTION] Produktion, ausgeweitet auf jedes Stadtzentrum innerhalb von 6 Feldern. Dieser Bonus zählt nur einfach pro Stadt und weitere Thermalbäder innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]Diese Stadt erhält +2 [ICON_SCIENCE] Wissenschaft, [ICON_CULTURE] Kultur, [ICON_TOURISM] Tourismus und weitere [ICON_AMENITIES] Annehmlichkeiten, wenn sich mindestens ein geothermaler Riss innerhalb der Stadtgrenzen befindet.'),
		('LOC_BUILDING_STADIUM_DESCRIPTION_UC_JNR',						'de_DE',	'+1 [ICON_Amenities] Annehmlichkeit für je 4 Spezialbezirke in dieser Stadt oder für je 2 Spezialbezirke in dieser Stadt, wenn mit Strom versorgt.[NEWLINE]+1 [ICON_GOLD] Gold für jedes Stadion oder Schwimmbad, wenn mit Strom versorgt.[NEWLINE]+2 [ICON_Tourism] Tourismus, wenn die [ICON_CITIZEN] Stadtbevölkerung mindestens 10 beträgt und +5 [ICON_Tourism] Tourismus, wenn [ICON_CITIZEN] 20 oder höher.'),
		('LOC_BUILDING_JNR_THEME_PARK_DESCRIPTION',						'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 9 Felder ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Kongresszentren innerhalb von 9 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+100% Nachbarschaftsbonus des Unterhaltungskomplexes dieser Stadt.'),
		('LOC_BUILDING_FERRIS_WHEEL_DESCRIPTION_UC_JNR',				'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Felder ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Riesenräder innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_Tourism] Tourismus.'),
		('LOC_BUILDING_JNR_MARINA_DESCRIPTION',							'de_DE',	'+1 [ICON_Amenities] Annehmlichkeit für jedes Fischerboot angrenzend an diesen Bezirk.[NEWLINE]+1 [ICON_CULTURE] Kultur und +1 [ICON_Tourism] Tourismus für jedes Fischerboot der Stadt.'),
		('LOC_BUILDING_AQUARIUM_DESCRIPTION_UC_JNR',					'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Felder ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Aquarien oder Kasinos innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_SCIENCE] Wissenschaft für jede Küstenressource, Schiffswrack, oder Riff in der Stadt.[NEWLINE]+1 [ICON_Tourism] Tourismus.[NEWLINE]+1 Anziehungskraft für jedes an ein Riff angrenzende Feld in der Stadt.'),
		('LOC_BUILDING_JNR_CASINO_DESCRIPTION',							'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Felder ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Aquarien oder Kasinos innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_GOLD] Gold von jedem Handelszentrum, Hafen, und Flugplatz in dieser Stadt.[NEWLINE]+1 [ICON_Tourism] Tourismus für jedes Wunder in dieser Stadt, das auf oder neben einem Küstenfeld gebaut wurde.'),
		('LOC_BUILDING_JNR_MARITIME_MUSEUM_DESCRIPTION',				'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 6 Felder ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Meereskundemuseen oder Kasinos innerhalb von 6 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+1 [ICON_GOLD] Gold von jedem Hafen, Arsenal und Hafenviertel in dieser Stadt.[NEWLINE]+1 [ICON_Tourism] Tourismus für jedes Wunder in dieser Stadt, das auf oder neben einem Küstenfeld gebaut wurde.'),
		('LOC_BUILDING_AQUATICS_CENTER_DESCRIPTION_UC_JNR',				'de_DE',	'+1 [ICON_Amenities] Annehmlichkeiten für je 4 Spezialbezirke in dieser Stadt oder für je 2 Spezialbezirke, wenn mit Strom versorgt.[NEWLINE]+1 [ICON_GOLD] Gold von jedem Stadion oder Schwimmbad, wenn mit Strom versorgt.[NEWLINE]+2 [ICON_Tourism] Tourismus wenn die [ICON_CITIZEN] Stadtbevölkerung mindestens 10 beträgt und +5 [ICON_Tourism] Tourismus wenn [ICON_CITIZEN] 20 oder höher.'),
		('LOC_BUILDING_JNR_FOOD_COURT_DESCRIPTION',						'de_DE',	'Zusätzliche [ICON_Amenities] Annehmlichkeiten werden auf weitere Stadtzentren innerhalb von 9 Felder ausgeweitet. Dieser Bonus zählt nur einfach pro Stadt und weitere Konzerthallen innerhalb von 9 Feldern gewähren keinen weiteren Bonus.[NEWLINE]+100% Nachbarschaftsbonus des Wasserparks in dieser Stadt.'),
