INSERT OR REPLACE INTO LocalizedText
  (Language, Tag, Text)
VALUES ('de_DE', 'LOC_GOVERNOR_PROMOTION_GARRISON_COMMANDER_DESCRIPTION', 'Verteidigende Einheiten im Gebiet der Stadt erhalten +6 [ICON_Strength] Kampfstärke. Eure anderen Städte in einem Umkreis von 9 Geländefeldern erhalten +2 Loyalität pro Runde für Eure Zivilisation.'),
	   ('de_DE', 'LOC_GOVERNOR_PROMOTION_AMBASSADOR_EMISSARY_DESCRIPTION', 'Andere Städte innerhalb von 9 Geländefeldern, die nicht Eure sind, verlieren 3 Loyalität pro Runde.');
