INSERT OR REPLACE INTO EnglishText
		(Tag,															Text)
VALUES	('LOC_BUILDING_ARENA_DESCRIPTION_UC_JNR',						'+1 [ICON_Amenities] Amenity if this city has an established Governor. Tourney Ground buildings within 6 tiles of this city do not provide additional bonuses.'),
		('LOC_BUILDING_JNR_TOURNEY_NAME',								'Tourney Ground'),
		('LOC_BUILDING_JNR_TOURNEY_DESCRIPTION',						'Bonus [ICON_Amenities] Amenity extends to each city center within 6 tiles. This bonus applies once to a city, and multiple copies of this or an Arena building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]+1 [ICON_Tourism] Tourism for every Ally this city has a Trade Route to.'),
		('LOC_BUILDING_TLACHTLI_DESCRIPTION_UC_JNR',					'A building unique to the Aztecs replacing both the Arena and the Tourney Ground.[NEWLINE]Provides 1 [ICON_Amenities] Amenity and an additional +1 [ICON_Amenities] Amenity if at war. +1 [ICON_Culture] Culture and a [ICON_GreatGeneral] Great General Point. +1 [ICON_FAITH] Faith for every original Capital owned.'),
		('LOC_BUILDING_ZOO_DESCRIPTION_UC_JNR',							'Bonus [ICON_Amenities] Amenity extends to each city center within 6 tiles. This bonus applies once to a city, and multiple copies of this or a Botanical Garden building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]+1 [ICON_SCIENCE] Science to each Resource tile in this city with a Pasture or Camp.[NEWLINE]+2 [ICON_Tourism] Tourism.'),
		('LOC_BUILDING_JNR_BOTANICAL_GARDEN_NAME',						'Botanical Garden'),
		('LOC_BUILDING_JNR_BOTANICAL_GARDEN_DESCRIPTION',				'Bonus [ICON_Amenities] Amenity extends to each city center within 6 tiles. This bonus applies once to a city, and multiple copies of this or a Zoo building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]+1 [ICON_SCIENCE] Science to each Resource tile in this city with a Farm, Plantation, or Lumber Mill.[NEWLINE]+2 [ICON_Tourism] Tourism.'),
		('LOC_BUILDING_THERMAL_BATH_DESCRIPTION_UC_JNR',				'A building unique to Hungary replacing both the Zoo and the Botanical Garden.[NEWLINE]+1 [ICON_AMENITIES] Amenity and +2 [ICON_PRODUCTION] Production extend to each city center within 6 tiles. These bonuses apply once to a city, and multiple copies of this building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]This city receives +2 [ICON_SCIENCE] Science, [ICON_CULTURE] Culture, [ICON_TOURISM] Tourism, and additional [ICON_AMENITIES] Amenities if there is at least one Geothermal Fissure in this city''s borders.'),
		('LOC_BUILDING_STADIUM_DESCRIPTION_UC_JNR',						'+1 [ICON_Amenities] Amenity for every 4 Specialty Districts in this city or for every 2 Specialty Districts in this city if powered.[NEWLINE]+1 [ICON_GOLD] Gold from every Stadium and Aquatic Center built if powered.[NEWLINE]+2 [ICON_Tourism] Tourism if the city [ICON_CITIZEN] population is 10 or higher and +5 [ICON_Tourism] Tourism if the city [ICON_CITIZEN] population is 20 or higher.'),
		('LOC_BUILDING_JNR_THEME_PARK_NAME',							'Convention Center'),
		('LOC_BUILDING_JNR_THEME_PARK_DESCRIPTION',						'Bonus [ICON_Amenities] Amenities extend to each city center within 9 tiles. This bonus applies once to a city, and multiple copies of this building within 9 tiles of a city center do not provide additional bonuses.[NEWLINE]Provides Gold equal to this district''s adjacency bonus.'),
		('LOC_BUILDING_FERRIS_WHEEL_DESCRIPTION_UC_JNR',				'Bonus [ICON_Amenities] Amenity extends to each city center within 6 tiles. This bonus applies once to a city, and multiple copies of this building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]+1 [ICON_Tourism] Tourism.'),
		('LOC_BUILDING_JNR_MARINA_NAME',								'Marina'),
		('LOC_BUILDING_JNR_MARINA_DESCRIPTION',							'+1 [ICON_Amenities] Amenity for every Fishing Boats improvement adjacent to this district.[NEWLINE]+1 [ICON_CULTURE] Culture to each Fishing Boats tile in this city.[NEWLINE]+1 [ICON_Tourism] Tourism for every Fishing Boats improvement in this city.'),
		('LOC_BUILDING_AQUARIUM_DESCRIPTION_UC_JNR',					'Bonus [ICON_Amenities] Amenity extends to each city center within 6 tiles. This bonus applies once to a city, and multiple copies of this or a Casino building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]+1 [ICON_SCIENCE] Science to each coastal resource, Shipwreck, and Reef tile in this city.[NEWLINE]+1 [ICON_Tourism] Tourism.[NEWLINE]+1 Appeal to tiles adjacent to Reefs in this city.'),
		('LOC_BUILDING_JNR_CASINO_NAME',								'Casino'),
		('LOC_BUILDING_JNR_CASINO_DESCRIPTION',							'Bonus [ICON_Amenities] Amenity extends to each city center within 6 tiles. This bonus applies once to a city, and multiple copies of this or a Aquarium building within 6 tiles of a city center do not provide additional bonuses.[NEWLINE]+1 [ICON_GOLD] Gold from every Commercial Hub, Harbor, and Aerodrome building in this city.[NEWLINE]+1 [ICON_Tourism] Tourism for each Wonder built in this city on or adjacent to a Coast tile.'),
