		('LOC_POLICY_JNR_RED_LIST_NAME',								'Red List'),
		('LOC_POLICY_JNR_RED_LIST_DESCRIPTION',							'+1 [ICON_Amenities] Amenity from Zoos, Botanical Gardens, and Aquariums, equal amount of [ICON_SCIENCE] Science if district has at least +4 adjacency bonus, equal amount of [ICON_CULTURE] Culture if city [ICON_CITIZEN] population is 15 or higher.'),
		('LOC_POLICY_JNR_RED_LIST_DESCRIPTION_MMSS',					'+1 [ICON_Amenities] Amenity from Zoos, Botanical Gardens, Aquariums, and Maritime Museums, equal amount of [ICON_SCIENCE] Science if district has at least +4 adjacency bonus, equal amount of [ICON_CULTURE] Culture if city [ICON_CITIZEN] population is 15 or higher.'),
		('LOC_POLICY_JNR_RED_LIST_DESCRIPTION_PRESERVE',				'+1 [ICON_Amenities] Amenity from Zoos, Botanical Gardens, and Aquariums, equal amount of [ICON_SCIENCE] Science if city has Preserve district, equal amount of [ICON_CULTURE] Culture if district has at least +4 adjacency bonus.'),
		('LOC_POLICY_JNR_RED_LIST_DESCRIPTION_PRESERVE_MMSS',			'+1 [ICON_Amenities] Amenity from Zoos, Botanical Gardens, Aquariums, and Maritime Museums, equal amount of [ICON_SCIENCE] Science if city has Preserve district, equal amount of [ICON_CULTURE] Culture if district has at least +4 adjacency bonus.');

