  (Language, Tag, Text)
VALUES ('en_US', 'LOC_GOVERNOR_PROMOTION_GARRISON_COMMANDER_DESCRIPTION', 'Units defending within the city''s territory get +6 [ICON_Strength] Combat Strength. Cities within 9 tiles gain +2 Loyalty per turn towards your civilization.'),
	   ('en_US', 'LOC_GOVERNOR_PROMOTION_AMBASSADOR_EMISSARY_DESCRIPTION', 'Other cities within 9 tiles and not owned by you lose 3 Loyalty per turn.');